import React, { Component } from "react";
import axios from "axios";
import AsteroidInfo from "./AsteroidInfo";

// const url =
  // `https://api.nasa.gov/neo/rest/v1/neo/${id}?api_key=pK54KJaxWhL7TN8V2CEK79taxNCH8QolYuHzYUnn`;

class MoreInfo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: "",
      id:""
    };
  }

  handleId = (event) => {
    this.setState({
      id: event.target.value
    });
  };
  handleSubmit = (event) => {
    var aId = `${this.state.id}`;
    console.log(aId);
    fetch(`https://api.nasa.gov/neo/rest/v1/neo/${id}?api_key=pK54KJaxWhL7TN8V2CEK79taxNCH8QolYuHzYUnn`, { Method: "GET" })
        .then((res) => res.json())
        .then((post) => {
          this.setState({ posts: post });
        });
  };
  // componentDidMount() {
  //   fetch(url, { Method: "GET" })
  //     .then((res) => res.json())
  //     .then((post) => {
  //       this.setState({ posts: post });
  //     });
  // }

  render() {
    const { posts } = this.state;
    return (
      <div>
        <h1>Asteroid Info</h1>
        <form onSubmit={this.handleSubmit}>
        <label>Enter Asteroid Id:</label>
          <br />
          <input
            type="text"
            placeholder="3542519"
            value={this.state.id}
            onChange={this.handleId}
          />
          <br />
          <button className="button" type="submit">
            Submit
          </button>
        </form>
        
        <AsteroidInfo datalist={this.state.posts} />
      </div>
    );
  }
}

export default MoreInfo;
